import { useState, useEffect, useCallback } from "react";
import classes from "./AvailableMeals.module.css";
import Card from "../UI/Card";
import MealItem from "./MealItem/MealItem";
import useHttp from "../../hooks/use-http";

const AvailableMeals = () => {
  const [meals, setMeals] = useState([]);
  // const [isLoading, setIsLoading] = useState(false);
  // const [error, setError] = useState(null);
  const { error, isLoading, sendRequest: sendMealsRequest } = useHttp();

  const fetchMealsHandler = async () => {
    // have a set is loading
    setIsLoading(true);
    setError(null);
    // has an error set

    try {
      const response = await fetch(
        "https://react-http-7b544-default-rtdb.asia-southeast1.firebasedatabase.app/meals.json"
      );

      if (!response.ok) {
        throw new Error("Something went wrong");
      }

      const data = await response.json();

      const loadedMeals = [];

      for (const key in data) {
        loadedMeals.push({
          id: key,
          name: data[key].name,
          description: data[key].description,
          price: data[key].price,
        });
      }

      setMeals(loadedMeals);
    } catch (error) {
      setError(error.message);
      setIsLoading(false);
      console.log(error.message);
      // This error message isn't saving to the setError ---
    }
    setIsLoading(false);
  };

  useEffect(() => {
    // fetchMealsHandler();
    sendMealsRequest({
      url: "https://react-http-7b544-default-rtdb.asia-southeast1.firebasedatabase.app/meals.json",
    });
  }, [sendMealsRequest]);

  const mealsList = meals.map((meal) => (
    <MealItem
      id={meal.id}
      key={meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price}
    />
  ));

  let content = (
    <Card>
      <p>no meals found...</p>
    </Card>
  );

  if (mealsList.length > 0) {
    content = <Card>{mealsList}</Card>;
  }

  if (error) {
    content = (
      <Card>
        <p>Error: {error}</p>
      </Card>
    );
  }

  if (isLoading) {
    content = (
      <Card>
        <p>loading...</p>
      </Card>
    );
  }

  return (
    <section className={classes.meals}>
      <ul>{content}</ul>
    </section>
  );
};

export default AvailableMeals;
