    // sendOrderRequest({
    //   url: 'https://react-http-7b544-default-rtdb.asia-southeast1.firebasedatabase.app/orders.json',
    //   method: 'POST', 
    //   headers: {
    //     "Content-Type" : "application/json"
    //   },
    //   body: {}
    // });
    // props.onCancel();
  };

  // form is valid
  let formIsValid = false;

  if (nameIsValid && addressIsValid && cityIsValid && postcodeIsValid) {
    formIsValid = true;
  }
  // submit form handler

  const nameInputClass = nameHasError
    ? `${classes.control} ${classes.invalid}`
    : classes.control;
  const addressInputClass = addressHasError
    ? `${classes.control} ${classes.invalid}`
    : classes.control;
  const cityInputClass = cityHasError
    ? `${classes.control} ${classes.invalid}`
    : classes.control;
  const postcodeInputClass = postcodeHasError
    ? `${classes.control} ${classes.invalid}`
    : classes.control;

  return (
    <form onSubmit={confirmHandler}>
      <div className={nameInputClass}>
        <label htmlFor="name">Name</label>
        <input
          type="text"
          id="name"
          onChange={nameChangeHandler}
          onBlur={nameBlurHandler}
          value={nameValue}
        />
        {nameHasError && (
          <p className={classes.invalidMessage}>must have a name</p>
        )}
      </div>
      <div className={addressInputClass}>
        <label htmlFor="address">Address</label>
        <input
          type="text"
          id="address"
          onChange={addressChangeHandler}
          onBlur={addressBlurHandler}
          value={addressValue}
        />
        {addressHasError && (
          <p className={classes.invalidMessage}>must have a valid city</p>
        )}
      </div>
      <div className={cityInputClass}>
        <label htmlFor="city">City</label>
        <input
          type="text"
          id="city"
          onChange={cityChangeHandler}
          onBlur={cityBlurHandler}
          value={cityValue}
        />
        {cityHasError && (
          <p className={classes.invalidMessage}>must have a valid city</p>
        )}
      </div>
      <div className={postcodeInputClass}>
        <label htmlFor="postcode">Post Code</label>
        <input
          type="text"
          id="postcode"
          onChange={postcodeChangeHandler}
          onBlur={postcodeBlurHandler}
          value={postcodeValue}
        />
        {postcodeHasError && (
          <p className={classes.invalidMessage}>must have a valid city</p>
        )}
      </div>
      <div className={classes.actions}>
        <button type="button" onClick={props.onCancel}>
          Cancel
        </button>
        <button
          className={classes.submit}
          type="submit"
          disabled={!formIsValid}
        >
          Confirm
        </button>
      </div>
    </form>
  );
};

export default Checkout;
