import classes from "./Checkout.module.css";
import useInput from "../../hooks/use-input";

const Checkout = (props) => {
  const confirmHandler = (event) => {
    event.preventDefault();
    console.log(event);
    // http request to firebase
    // write a custom hook to handel the http requests.
  }

  const isValidValue = value => value.trim() !== '';

  const {
    value: nameValue,
    hasError: nameHasError,
    isValid: nameIsValid,
    valueChangeHandler: nameChangeHandler,
    inputBlurHandler: nameBlurHandler,
    reset: resetName
  } = useInput(isValidValue)

  //write a custom input to check validation

  // form is valid
  let formIsValid = false;

  if (nameIsValid) {
    formIsValid = true;
  }
  // submit form handler

  const nameInputClass = nameHasError ? `${classes.control} ${classes.invalid}` : classes.control

  return (
    <form onSubmit={confirmHandler}>
      <div className={nameInputClass}>
        <label htmlFor="name">Name</label>
        <input 
          type="text" 
          id="name"
          onChange={nameChangeHandler}
          onBlur={nameBlurHandler}
          value={nameValue}
        />
        {nameHasError && <p className={classes.invalidMessage}>must have a name</p>}
      </div>
      <div className={classes.control}>
        <label htmlFor="address">Address</label>
        <input type="text" id="address" />
      </div>
      <div className={classes.control}>
        <label htmlFor="city">City</label>
        <input type="text" id="city" />
      </div>
      <div className={classes.control}>
        <label htmlFor="postcode">Post Code</label>
        <input type="text" id="postcode" />
      </div>
      <div className={classes.actions}>
        <button type="button" onClick={props.onCancel}>Cancel</button>
        <button className={classes.submit} type="submit" disabled={!nameIsValid}>Confirm</button>
      </div>
    </form>
  );
};

export default Checkout;
