import { useState, useEffect, useCallback } from "react";
import classes from "./AvailableMeals.module.css";
import Card from "../UI/Card";
import MealItem from "./MealItem/MealItem";

const AvailableMeals = () => {
  const [meals, setMeals] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchMealsHandler = useCallback(async () => {
    // have a set is loading
    setIsLoading(true);
    setError(null);
    // has an error set

    const response = await fetch(
      "https://react-http-7b544-default-rtdb.asia-southeast1.firebasedatabase.app/meals.json"
    );

    if (!response.ok) {
      throw new Error("Something went wrong");
    }

    const data = await response.json();

    const loadedMeals = [];

    for (const key in data) {
      loadedMeals.push({
        id: key,
        name: data[key].name,
        description: data[key].description,
        price: data[key].price,
      });
    }

    setMeals(loadedMeals);
    setIsLoading(false);
    console.log(error, "line 45");
  }, [error]);

  useEffect(() => {
    try {
      fetchMealsHandler();
    } catch (error) {
      setError(error);
      setIsLoading(false);
    }
  }, []);

  const mealsList = meals.map((meal) => (
    <MealItem
      id={meal.id}
      key={meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price}
    />
  ));

  const message = error ? (
    <p className={classes.MealsLoading}>{error}</p>
  ) : (
    <p>loading...</p>
  );

  return (
    <section className={classes.meals}>
      <ul>
        <Card>{!isLoading ? mealsList : message}</Card>
      </ul>
    </section>
  );
};

export default AvailableMeals;
